import React from "react";
import doctors from "../img/doctor-undraw.svg";


export default function Quote() {
  return (
    <div class="grid grid-cols-1 md:grid-cols-2 w-full border bg-blue-700 py-5 px-5 md:py-36 md:px-36">
    <div>
      <h2 class="py-12 mb-2 text-2xl md:text-4xl font-bold text-white text-center md:text-left">
        “We are healthcare professionals with over 45 years of
        experience. Together we use genetics, lifestyle optimization,
        and disease prevention to help YOU live a fuller and happier life.”
      </h2>
      </div>
      <div>
      <img src={doctors} className="pt-12 md:pt-0 md:w-4/5 w-3/5 md:ml-auto md:mx-0 mx-auto" />
      </div>
    </div>
  );
}
